public class castSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float fX = 4.0F;
		double dX = -1.5D;
		float fY = (float) (fX + dX);
		System.out.println("(float) (fX + dX) = " + fY);
		double dY = (double) (fX + dX);
		System.out.println("(double) (fX + dX) = " + dY);
	}

}
