public class bitWiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int iX = 5;
		System.out.println("iX = " + iX);
		System.out.println("iX << 1 = " + (iX << 1));
		System.out.println("iX << 2 = " + (iX << 2));
		System.out.println("iX << 3 = " + (iX << 3));
		System.out.println("iX << 4 = " + (iX << 4));
		System.out.println("iX >> 1 = " + (iX >> 1));
		System.out.println("iX >> 2 = " + (iX >> 2));
		System.out.println("~iX = " + (~iX));
		
		int iY = -1;
		System.out.println("iY = " + iY);
		System.out.println("iY >> 1 = " + (iY >> 1));
		System.out.println("iY >>> 1 = " + (iY >>> 1));
	}

}
